	if(hotspot[get(i)].style === 'hstxtstyle' OR hotspot[get(i)].style === 'startstyle',
		set(curhs, get(hotspot[get(i)].name));
		if(curlang == fr,
			set(hotspot[get(curhs)].url, get(hotspot[get(curhs)].frimage));
			,
			set(hotspot[get(curhs)].url, get(hotspot[get(curhs)].engimage));
		);
	);
	if(hotspot[get(i)].style === 'thumb' ,
		set(curhs, get(hotspot[get(i)].name));
		if(curlang == fr,
			txtadd(mysound,'fr/',get(hotspot[get(curhs)].music));
			copy(hotspot[get(curhs)].txtsound, mysound);
			,
			txtadd(mysound,'en/',get(hotspot[get(curhs)].music));
			copy(hotspot[get(curhs)].txtsound, mysound);
		);
	);
);

